    #[starknet::interface]
    trait ISimpleStorage<TContractState> {
        fn set(ref self: TContractState, x: u128);
    }

    #[starknet::contract]
    mod SimpleStorage {

    #[storage]
    struct Storage {
        stored_data: u128
    }

    #[external(v0)]
    impl SimpleStorage of super::ISimpleStorage<ContractState> {
        fn set(ref self: ContractState, x: u128) {
            self.stored_data.write(x);
        }
    }
}
